    const [selectedCourt, setSelectedCourt] = useState<Court>({name: "", url: ""})

    const [activeVoivodeship, setActiveVoivodeship] = useState<Voivodeship | null>(null);

    function activateVoivodeship(voivodeship_id: number): void {

        const width: number = window.innerWidth;

        const selected_voivodeship: Voivodeship | undefined = voivodeships.find(item => item.id === voivodeship_id);

        if(width < 768 && selected_voivodeship){

            setActiveVoivodeship(selected_voivodeship);

        }

    }

    function showCityDescription(event: React.MouseEvent<SVGCircleElement, MouseEvent>, voivodeship_id: number, city_id: number): void {

        const width: number = window.innerWidth;

        if(width <= 768 && !activeVoivodeship){

            return;

        }

        const selected_voivodeship: Voivodeship | undefined = voivodeships.find(item => item.id === voivodeship_id);

        const selected_city: City | undefined = selected_voivodeship?.city.find(city => city.id === city_id);

        if(selected_city){

            const svg: SVGSVGElement | null = event.currentTarget.ownerSVGElement;

            if(!svg) return;

            const pt: DOMPoint = svg.createSVGPoint();

            pt.x = selected_city.cx;

            pt.y = selected_city.cy;

            const ctm: DOMMatrix | null = svg.getScreenCTM();

            if(!ctm) return;

            const screenPoint: DOMPoint = pt.matrixTransform(ctm);

            setSelectedCourt({ name: selected_city.court_name, url: selected_city.court_url });

            setClickPosition({ x: screenPoint.x, y: screenPoint.y });

        }

    }

